import { Component } from '@angular/core';
import { Persona } from './persona.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  titulo:string='Listado de personas';
  personas:Persona[]=[new Persona('Victor','CG'), new Persona('Grace','CG'), new Persona('Elon','CG')];
  nombreInput:string='';
  apellidoInput:string='';

  agregarPersona(){
    let persona1=new Persona(this.nombreInput,this.apellidoInput);
    this.personas.push(persona1);
  }
}
<div class="container">
  <div class="col">
    <h3>{{titulo}}</h3>
  </div>
</div>

<hr>

<form action="" class="form-row">
  <div class="form-group col-md-2">
    <!-- <label for="nombre" class="sr-only">Nombre:</label> -->
    <input type="text" name="nombre" id="nombre" placeholder="Nombre"
    class="form-control" [(ngModel)]="nombreInput">
  </div>

  <div class="form-group col-md-2">
    <!-- <label for="nombre" class="sr-only">Nombre:</label> -->
    <input type="text" name="apellido" id="apellido" placeholder="apellido"
    class="form-control" [(ngModel)]="apellidoInput">
  </div>

  <div class="col">
    <button type="submit" class="btn btn-primary" (click)="agregarPersona()">Agregar</button>
  </div>
</form>

<div class="container">
  <hr>
  <div class="row">
    <div class="col">
      <div *ngFor="let persona of personas, let i=index">
        {{i+1}}: {{persona.nombre}}  {{persona.apellido}}
      </div>
    </div>
  </div>
</div>
