import { Component } from '@angular/core';
import { Persona } from './persona.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  titulo:string='Listado de personas';
  personas:Persona[]=[new Persona('Victor','CG'), new Persona('Grace','CG'), new Persona('Elon','CG')];


  personaAgregada(persona:Persona){
    this.personas.push(persona);
  }
}
<div class="container">
  <div class="col">
    <h3>{{titulo}}</h3>
  </div>
  <hr>
</div>

<app-formulario
  (personaCreada)="personaAgregada($event)">
</app-formulario>

<app-persona
  *ngFor="let personaElemento of personas, let iElemento=index"
  [persona]="personaElemento"
  [i]="iElemento">
</app-persona>
