import { PersonasService } from '../PersonasService.service';

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.css'],
  //providers:[LoggingService] //Inyeccion de dependencia

